Controller:
["$scope", "$http", "$timeout", function ($scope, $http, $timeout) {
    $scope.ref = {};
    $scope.icons = [];
    if(!$scope.$root.icons){
        fetch("https://cdn.jsdelivr.net/npm/@mdi/font@6.2.95/css/materialdesignicons.min.css").then(r => r.text()).then(r =>{
            $scope.$root.icons = $scope.icons = Array.from(r.matchAll(/\.([^:]*)\:\:before/g)).map(c => c[1]);
            $scope.list = doSearch();
            $scope.$applyAsync();
        });
    }else{
        $scope.icons = $scope.$root.icons;
    }
    $scope.copy = function(c){
        navigator.clipboard.writeText(c);
        $scope.showMessage('Copied "'+ c +'"');
    }
    function doSearch(search){
        if(!search) return $scope.icons.slice(0, 96);
        search = search.toLowerCase();
        return $scope.icons.filter(c => c.includes(search)).map((c, i) => ({
            icon: c,
            index: i,
            match: c.editDistance(search)
        })).orderBy(c => c.match * 1000000 + c.index).map(c => c.icon).slice(0, 96);
    }
    let to = null;
    let lastSearch = null;
    $scope.doSearch = function(txt){
        to && $timeout.cancel(to);
        if(txt != lastSearch){
            to = $timeout(()=>{
                $scope.list = doSearch(txt);
                lastSearch = txt;
                to = null;
            }, 500);
        }
    }
    $scope.list = doSearch();
}]
